import React, { Component } from 'react';

import Card from './components/Card'
import Select from './components/Select';
import { movies$ } from './app/movies'

import './App.css'

class App extends Component {

  constructor (props) {
    super(props)

    this.state = {
      movies: [],
      categories: [],
      selectedCategories: []
    }
    this.deleteHandler = this.deleteHandler.bind(this)
    this.onChangeHandler = this.onChangeHandler.bind(this)
  }

  componentDidMount () {
    movies$.then(data => {
      this.setState({
        movies: data,
        categories: [...new Set(data.map(entry=>entry.category)).values()]
      })
    })
  }

  deleteHandler (id) {
    this.setState({
      movies: this.state.movies.filter(movie => movie.id !== id)
    })
  }

  onChangeHandler(values)
  {
    this.setState({
      selectedCategories: values
    })
  }

  render() {
    return (
      <div className='container'>
        <div>
          <Select onChange={this.onChangeHandler} categories={this.state.categories} ></Select>
        </div>

      </div>
    );
  }
}

export default App;
