import React, { Component } from 'react';

import Card from './components/Card';
// import Select from './components/Select';
import { movies$ } from './app/movies'

import './App.css'

class App extends Component {
  constructor (props) {
    super(props)

    this.state = {
      movies: [],
      categories: [],
      selectedCategories: []
    }
    this.deleteHandler = this.deleteHandler.bind(this)
    this.onChangeHandler = this.onChangeHandler.bind(this)
  }

  componentDidMount () {
    movies$.then(data => {
      this.setState({
        movies: data,
        categories: [...new Set(data.map(entry=>entry.category)).values()]
      })
    })
  }

  deleteHandler (id) {
    this.setState({
      movies: this.state.movies.filter(movie => movie.id !== id)
    })
  }

  onChangeHandler(values)
  {
    this.setState({
      selectedCategories: values
    })
  }

  render () {
    return (
      <div className='container'>
        <div className="row  flex-container">
          <div>
            <Select onChange={this.onChangeHandler} categories={this.state.categories} ></Select>
          </div>
        </div>
        <div>
        </div>
        <div className='row  flex-container'>
          {this.state.movies.filter((movie)=>{

            return this.state.selectedCategories.includes(movie.category) || (this.state.selectedCategories.length === 0)

          }).map(movie => {
            return (
              <div className='col'>
                <Card
                  onDelete={this.deleteHandler}
                  key={movie.id}
                  {...movie}
                />
              </div>
            )
          })}
        </div>
      </div>
    )
  }
}

class Select extends Component{

  constructor(props)
  {
    super(props)
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e)
  {
    var options = e.target.options;
    var values = [];
    for (var i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        values.push(options[i].value);
      }
    }
    this.props.onChange(values);
  }

  render()
  {
      return <select onChange={this.handleChange}  className="select" multiple>

        {this.props.categories.map((category,index)=>{
          return <option key={index} value={category}>{category}</option>
        })}

      </select>
  }

}

export default App;
