import React, { Component } from 'react'
import LikeButton from './LikeButton';


class Card extends Component {
  constructor (props) {
    super(props)

    this.state = {
      likes:props.likes,
      dislikes: props.dislikes
    }
  }

  clickHandler (value) {
    if (!value) {
      this.setState(prev=>{
        return {
          likes: prev.likes + 1,

        }
      })
    } else {
      this.setState(prev=>{
        return {
          likes: prev.likes - 1,
        }
      })
    }
  }

  deleteHandler(i, e) {
    e.preventDefault();
    this.props.onDelete(this.props.blogPosts[i].id);
  };
  remove = (rowId) => {
    // Array.prototype.filter returns new array
    // so we aren't mutating state here
    const arrayCopy = this.state.data.filter((row) => row.id !== rowId);
    this.setState({data: arrayCopy});
  };


  render () {
    const rows = this.state.data.map( (rowData) => <Row remove={this.remove} {...rowData } />);

    return (
      <div className='card'>
        <h1 className='card__title'>{this.props.title}</h1>
        <p className="card__subtitle" >
          {this.props.category}
        </p>

        <div className='card__footer'>
          <div> {this.state.likes} Likes </div>
          <div> {this.state.dislikes} Dislikes </div>
        </div>
        <div className="remove"></div>

        <LikeButton click={this.clickHandler.bind(this)} ></LikeButton>
        <button class="btn btn--delete" onclick={this.props.onDelete.bind(this,this.props.id)}>âœ—</button>

      </div>
    )
  }
}



export default Card
