import React,{ Component } from "react";

class LikeButton extends Component{
  constructor(props)
  {
    super(props);
    this.state = {
      liked: false
    }
    this.handlerClick = this.handlerClick.bind(this)

    // this.clickHandler = this.clickHandler.bind(this)
  }

  // handlerClick(){

  //   this.setState((prev)=>{

  //     this.props.click(this.state.liked)

  //     return {
  //       liked: !prev.liked
  //     }
  //   })

  // }

  handlerClick() {
    this.setState({
      liked: !this.state.liked
    });
  }


  render(){
    let style = !this.state.liked ? { color: 'white' } : {color : 'black'};

    // const text = this.state.liked ? 'liked' : 'haven\'t liked';
    const label = this.state.liked ? 'Unlike' : 'Like'

    // let style = !this.state.liked ? { color: 'white' } : {color : 'black'};

    // return <Button class="btn btn--togglelike" style={style} click={this.clickHandler}>❤</Button> 
    return(
      <div className="customContainer">
        <button class="btn btn--togglelike" style={style} click={this.handlerClick}>
          {label}</button>

      </div>
    );



  }

}


export default LikeButton;
