import React, { Component } from 'react'

// import LikeButton from './LikeButton'

class Card extends Component {
  constructor (props) {
    super(props)

    this.state = {
      likes:props.likes,
      dislikes: props.dislikes
    }
  }

  clickHandler (value) {
    if (!value) {
      this.setState(prev=>{
        return {
          likes: prev.likes + 1,

        }
      })
    } else {
      this.setState(prev=>{
        return {
          likes: prev.likes - 1,
        }
      })
    }
  }

  render () {
    return (
      <div className='card'>
        <h1 className='card__title'>{this.props.title}</h1>
        <p className="card__subtitle" >
          {this.props.category}
        </p>

        <div className='card__footer'>
          <div> {this.state.likes} Likes </div>
          <div> {this.state.dislikes} Dislikes </div>
        </div>

        <LikeButton/>

        <button class="btn btn--delete" click={this.props.onDelete.bind(this,this.props.id)}>âœ—</button>

      </div>
    )
  }
}

class LikeButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = { liked: false };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event) {
    this.setState({ liked: !this.state.liked});
  }
  render() {
    let buttonText = this.state.liked? 'Unlike': 'Like';
    return (
      <button onClick={this.handleClick} className="like">
        <i className="fa fa-heart"></i>&nbsp;
        {buttonText}</button>
     );
  }
}

export default Card
