import React, { Component } from 'react';

import Card from './components/Card';
import Select from './components/Select';
import { movies$ } from './app/movies'

import './App.css'

class App extends Component {
  constructor (props) {
    super(props)

    this.state = {
      movies: [],
      categories: [],
      selectedCategories: []
    }
    this.deleteHandler = this.deleteHandler.bind(this)
    this.onChangeHandler = this.onChangeHandler.bind(this)
  }

  componentDidMount () {
    movies$.then(data => {
      this.setState({
        movies: data,
        categories: [...new Set(data.map(entry=>entry.category)).values()]
      })
    })
  }

  deleteHandler (id) {
    this.setState({
      movies: this.state.movies.filter(movie => movie.id !== id)
    })
  }

  onChangeHandler(values)
  {
    this.setState({
      selectedCategories: values
    })
  }

  render () {
    return (
      <div className='container'>
        <container>
          <Row>
            <Col sm={8}>sm=8</Col>
            <Col sm={4}>sm=4</Col>
          </Row>
          <Row>
            <Col sm>sm=true</Col>
            <Col sm>sm=true</Col>
            <Col sm>sm=true</Col>
          </Row>
        </container>
        <div className="row  flex-container">
          <div className='col2'>
            <Select onChange={this.onChangeHandler} categories={this.state.categories} ></Select>
          </div>
          <div className="col">
            <div className='row  Container'>
            {this.state.movies.filter((movie)=>{

              return this.state.selectedCategories.includes(movie.category) || (this.state.selectedCategories.length === 0)

            }).map(movie => {
              return (
                <div className='col12'>
                  <Card
                    onDelete={this.deleteHandler}
                    key={movie.id}
                    {...movie}
                  />
                </div>
              )
            })}
          </div>
          </div>
        </div>


      </div>
    )
  }
}

export default App;
